package de.osca.android.district.pressrelease.data.model

import com.parse.ParseClassName
import com.parse.ParseObject
import com.parse.ParseRelation
import com.parse.ktx.delegates.intAttribute
import com.parse.ktx.delegates.nullableStringAttribute
import com.parse.ktx.delegates.relationAttribute
import com.parse.ktx.delegates.stringAttribute
import de.osca.android.district.core.data.model.dateAttribute
import de.osca.android.district.core.data.model.district.District
import java.util.Date

@ParseClassName("PressRelease")
class PressRelease : ParseObject() {
    val title: String by stringAttribute()
    val summary: String? by nullableStringAttribute()
    val date: Date by dateAttribute()
    val url: String? by nullableStringAttribute()
    val imageUrl: String? by stringAttribute()
    val content: String by stringAttribute()
    val readingTime: Int by intAttribute()
    val districts: ParseRelation<District> by relationAttribute()

    // auslagern??
    companion object {
        fun dummy(): PressRelease {
            val pressRelease = PressRelease()
            pressRelease.put("title", "Super Duper Ultra Nice Title")
            pressRelease.put("summary", "Summary")
            pressRelease.put("date", Date())
            @Suppress("ktlint:standard:max-line-length")
            pressRelease.put(
                "url",
                "https://images.pexels.com/photos/1366919/pexels-photo-1366919.jpeg?auto=compress&cs=tinysrgb&w=1260&h=750&dpr=1",
            )
            pressRelease.put("imageUrl", "")
            pressRelease.put("content", LOREMIPSUM)
            pressRelease.put("readingTime", 5)

            return pressRelease
        }
    }
}

